import React, { Fragment,useState, useEffect } from "react";
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { alpha, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import logo from "./image/TESARK_Logo@3x.png"
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import CheckBoxOutlineBlankIcon from '@material-ui/icons/CheckBoxOutlineBlank';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
const axios = require('axios');


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
  header:{
    margin:"20px"
  },
  header1:{
    color:"red",
    fontSize:"20px"
  }
}));

export default function SearchAppBar() {
  const classes = useStyles();

  const [type, setType] = useState("");
  console.log("123",type)

  useEffect(() => {
    category()
  })
// category get
const category =()=>{
  axios.get(`http://localhost:8081`)
  .then(res => {
  console.log("res",res)
    setType(res.data)
  })

}


  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
        <image src={logo}></image>
          <Typography className={classes.title} variant="h6" noWrap>
            TESARK
          </Typography>
    
          <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase
              placeholder="Search…"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              inputProps={{ 'aria-label': 'search' }}
            />
          </div>
        </Toolbar>
      </AppBar>

      <Grid container spacing={3}>
      <Grid item xs={12} sm={12} md={12} lg={12}>
      <Typography className={classes.header} variant="h4" noWrap>
            RESTAURANT IN INDIA <span className={classes.header1}>/TOTAL RESTAURANT</span>  
          </Typography>
        </Grid>
 
        <Grid item xs={6} sm={6} md={6} lg={4}>
        <Grid item xs={6} sm={6} md={6} lg={4}>
          <Typography>category</Typography>
        {type.length >0 && type.map((item,index) => (
                            <FormControlLabel
                         control={
                        <Checkbox
                       icon={<CheckBoxOutlineBlankIcon fontSize="small" />}
                       checkedIcon={<CheckBoxIcon fontSize="small" />}
                        name={item.id}
                        checked={item.status}
                        onChange={(e) => chkhandleChange("Status",e,index,item)}
                               />
                           }
                           label={item.value}
                           />
                           ))}
        </Grid>

     
        </Grid>
        <Grid item xs={6} sm={6} md={6} lg={8}>
        hello
        </Grid>
 
      </Grid>
    </div>
  );
}
