import React, { Fragment,useState, useEffect } from "react";
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { alpha, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import logo from "./image/istockphoto-1220017909-170667a.jpg"
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import CheckBoxOutlineBlankIcon from '@material-ui/icons/CheckBoxOutlineBlank';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import Divider from '@material-ui/core/Divider';
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import NativeSelect from '@material-ui/core/NativeSelect';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import SearchBar from "material-ui-search-bar";
import './style.css'

import axios from 'axios';
const location = [
  { id: "1", value: 'chennai' },
  { id: "2", value: 'Maduari' },
  { id: "3", value: 'Ambur' },
  { id: "4", value: 'Dindigul' },
  { id: "5", value: 'Vellore' },

];
const daysarr = [
  { id: "1", value: 'Veg', status:false },
  { id: "2", value: 'Non veg' ,status:false},
  { id: "3", value: 'Fruit' ,status:false},

];



const useStyles = makeStyles((theme) => ({
  left_session:{
    padding:theme.spacing(2),
    maxWidth:"100%"
  },
  location:{
    marginTop:theme.spacing(4)
  },
  right_side:{
    padding:theme.spacing(4),
    maxWidth:"100%"
 
  },
  sub_header:{
    padding:theme.spacing(2),
    maxWidth:"100%"
  },
  ran_slider:{
    marginTop:theme.spacing(4)

  },
  media: {
    height: 140,
  },
  list:{
    marginTop:theme.spacing(2) 
  },
  header:{
    padding:theme.spacing(2),

  },
  header1:{
    fontSize:"20px"

  }
 
}));


export default function SearchAppBar() {
  const classes = useStyles();
  const [days,setdays]=useState(daysarr);
  const [type, setType] = useState("");
  const [list, setList] = useState("");
console.log("days",days)

  useEffect(() => {
    category()
    sort_list()
  }, []);
// category get

const category =()=>{
  axios.get(`http://localhost:8081`)
  .then(res => {
     
    setType(res.data)
  })
  .catch(err=> {
    console.log("error")

  })

}


// sort list
const sort_list =()=>{
  var selectedWeekdays = [];
  days.map(function (value, index1) {
    
    if(value.status == true){
     
     selectedWeekdays.push(value.id);

    }
   });
   console.log("selectedWeekdays",)
  axios.get(`http://localhost:8081/app/filter`,selectedWeekdays)
  .then(res => {
      
       setList(res.data)
  })
  .catch(err=> {
    console.log("error")

  })

}

const chkhandleChange = (prop, event, index, item) => {
  let newArr = [...days];
  days.map((data) => {
    if(event.target.checked === true){
    newArr[index].status = event.target.checked;
    }else{
      newArr[index].status = false;
    }
  });

  setdays(newArr); 
  sort_list()
};

  return (
    <div className={classes.root}>


      <Grid container spacing={0}>
      <Grid item xs={12} sm={12} md={12} lg={12}>
      <Grid container spacing={0}>
      <Grid item xs={12} sm={12} md={8} lg={8}>
      <Typography className={classes.header} variant="h4" noWrap>
            RESTAURANT IN INDIA <span className={classes.header1}>/TOTAL RESTAURANT</span>  
          </Typography>
      </Grid>
      <Grid item xs={12} sm={12} md={4} lg={4} className={classes.header}>
      <SearchBar 
    // value={this.state.value}
    // onChange={(newValue) => this.setState({ value: newValue })}
    // onRequestSearch={() => doSomethingWith(this.state.value)}
  />
      </Grid>
      </Grid>
        </Grid>
 
 {/* left side session */}
        <Grid item xs={6} sm={4} md={4} lg={3}>
        <Grid item xs={6} sm={6} md={3} lg={3} className={classes.left_session}>
          <Typography>category</Typography>
                  {/* {type.length >0 && type.map((item,index) => (
                            <FormControlLabel
                         control={
                        <Checkbox
                       icon={<CheckBoxOutlineBlankIcon fontSize="small" />}
                       checkedIcon={<CheckBoxIcon fontSize="small" />}
                        name={item.id}
                        checked={item.status}
                        onChange={(e) => chkhandleChange("Status",e,index,item)}
                               />
                           }
                           label={item.category_name}
                           />
                           ))} */}

         
<FormGroup>
        {days.length >0 && days.map((item,index) => (    
              <>    
    
    <FormControlLabel
                         control={
                        <Checkbox
                       icon={<CheckBoxOutlineBlankIcon fontSize="small" />}
                       checkedIcon={<CheckBoxIcon fontSize="small" />}
                        name={item.id}
                        checked={item.status}
                        onChange={(e) => chkhandleChange("Status",e,index,item)}
                               />
                           }
                           label={item.value}
                           />
                         
                        </>
        ))}
        </FormGroup>


        <Typography className={classes.location}>Location</Typography>
        <FormGroup>
        {days.length >0 && days.map((item,index) => (    
              <>    
    
    <FormControlLabel
                         control={
                        <Checkbox
                       icon={<CheckBoxOutlineBlankIcon fontSize="small" />}
                       checkedIcon={<CheckBoxIcon fontSize="small" />}
                        name={item.id}
                        checked={item.status}
                        onChange={(e) => chkhandleChange("Status",e,index,item)}
                               />
                           }
                           label={item.value}
                           />
                         
                        </>
        ))}
        </FormGroup>

        <Typography id="discrete-slider-custom" gutterBottom  className={classes.ran_slider}>
        Custom marks
      </Typography>
      <Slider
        defaultValue={20}
        // getAriaValueText={valuetext}
        aria-labelledby="discrete-slider-custom"
        step={10}
        valueLabelDisplay="auto"
        // marks={marks}
      />
        </Grid>

     
        </Grid>
      
        <Grid item xs={6} sm={8} md={8} lg={9} className={classes.right_side}>
        <Divider />
        <Grid item xs={6} sm={6} md={8} lg={12} className={classes.sub_header}>
      
        <Grid item xs={6} sm={4} md={2} lg={2} >
       sort list
        </Grid>     
      </Grid>        
       
        <Divider />
       
        <Grid item xs={12} sm={12} md={12} lg={12} className={classes.list}>
        <Grid container spacing={2}>
        {list.length >0 && list.map((item,index) => (    
              <>  
       <Grid item xs={12} sm={12} md={4} lg={4} >
     
        <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={logo}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {item.food_name}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
      <Typography gutterBottom variant="h5" component="h2">
      {item.cost}
      </Typography>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
